import { useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    gender: "",
    skills: [],
    country: "",
    dob: "",
    message: "",
    terms: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name === "skills") {
      setFormData(prev => ({
        ...prev,
        skills: checked
          ? [...prev.skills, value]
          : prev.skills.filter(s => s !== value)
      }));
    } else if (type === "checkbox") {
      setFormData({ ...formData, [name]: checked });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log("ðŸ“¤ Sending data:", formData); // ðŸ‘ˆ DEBUG LINE

    await axios.post("http://localhost:5000/submit-form", formData);
    alert("All data submitted successfully");
  };

  return (
    <div className="form-container">
      <h2>Complete Animated Form</h2>

      <form onSubmit={handleSubmit}>

        {/* NAME */}
        <input
          name="name"
          placeholder="Name"
          onChange={handleChange}
        />

        {/* EMAIL */}
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
        />

        {/* PASSWORD */}
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
        />

        {/* GENDER */}
        <div className="radio-group">
          <label>
            <input
              type="radio"
              name="gender"
              value="Male"
              onChange={handleChange}
            /> Male
          </label>

          <label>
            <input
              type="radio"
              name="gender"
              value="Female"
              onChange={handleChange}
            /> Female
          </label>
        </div>

        {/* SKILLS */}
        <div className="checkbox-group">
          <label>
            <input
              type="checkbox"
              name="skills"
              value="HTML"
              onChange={handleChange}
            /> HTML
          </label>

          <label>
            <input
              type="checkbox"
              name="skills"
              value="CSS"
              onChange={handleChange}
            /> CSS
          </label>

          <label>
            <input
              type="checkbox"
              name="skills"
              value="React"
              onChange={handleChange}
            /> React
          </label>
        </div>

        {/* COUNTRY */}
        <select name="country" onChange={handleChange}>
          <option value="">Select Country</option>
          <option value="India">India</option>
          <option value="USA">USA</option>
        </select>

        {/* DOB */}
        <input
          type="date"
          name="dob"
          onChange={handleChange}
        />

        {/* MESSAGE */}
        <textarea
          name="message"
          placeholder="Message"
          onChange={handleChange}
        />

        {/* TERMS */}
        <label className="terms">
          <input
            type="checkbox"
            name="terms"
            onChange={handleChange}
          />
          I accept terms & conditions
        </label>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default App;
