import { useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    gender: "",
    skills: [],
    country: "",
    dob: "",
    message: "",
    terms: false
  });

  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name === "skills") {
      setFormData(prev => ({
        ...prev,
        skills: checked
          ? [...prev.skills, value]
          : prev.skills.filter(s => s !== value)
      }));
    } else {
      setFormData({ ...formData, [name]: type === "checkbox" ? checked : value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.password) {
      setError("âš  Please fill all required fields");
      return;
    }
    if (!formData.terms) {
      setError("âš  Please accept terms & conditions");
      return;
    }

    setError("");

    await axios.post("http://localhost:5000/submit-form", formData);
    alert("ðŸŽ‰ Form submitted with animation!");
  };

  return (
    <div className="form-container">
      <h2>Animated MERN Form</h2>

      {error && <div className="error">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input name="name" placeholder="Name*" onChange={handleChange} />
        </div>

        <div className="form-group">
          <input name="email" placeholder="Email*" onChange={handleChange} />
        </div>

        <div className="form-group">
          <input type="password" name="password" placeholder="Password*" onChange={handleChange} />
        </div>

        <div className="form-group terms">
          <input type="checkbox" name="terms" onChange={handleChange} />
          <span>I accept terms & conditions</span>
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default App;
