import React, { useState } from "react";

function App() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    gender: "",
    skills: [],
    country: "",
    dob: "",
    message: "",
    terms: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name === "skills") {
      setFormData((prev) => ({
        ...prev,
        skills: checked
          ? [...prev.skills, value]
          : prev.skills.filter((s) => s !== value),
      }));
    } else {
      setFormData({
        ...formData,
        [name]: type === "checkbox" ? checked : value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/submit-form", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });

    alert("Form Submitted");
  };

  return (
    <div style={{ width: "400px", margin: "40px auto" }}>
      <h2>Full Stack Form</h2>

      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" onChange={handleChange} /><br />
        <input name="email" placeholder="Email" onChange={handleChange} /><br />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} /><br />

        <label>
          <input type="radio" name="gender" value="Male" onChange={handleChange} /> Male
        </label>
        <label>
          <input type="radio" name="gender" value="Female" onChange={handleChange} /> Female
        </label>

        <br />

        <label>
          <input type="checkbox" name="skills" value="HTML" onChange={handleChange} /> HTML
        </label>
        <label>
          <input type="checkbox" name="skills" value="React" onChange={handleChange} /> React
        </label>

        <br />

        <select name="country" onChange={handleChange}>
          <option value="">Select Country</option>
          <option value="India">India</option>
          <option value="USA">USA</option>
        </select>

        <br />

        <input type="date" name="dob" onChange={handleChange} /><br />

        <textarea name="message" placeholder="Message" onChange={handleChange}></textarea><br />

        <label>
          <input type="checkbox" name="terms" onChange={handleChange} /> Accept Terms
        </label>

        <br /><br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
